// Kargo Intelligence Engine - Domain and Content Analysis

// Initialize core mappings
const domainToAgency = {
  'amazon.com': { agency: 'Client Direct', agencyId: '0010h00001ZIT8BAAX' },
  'kinesso.com': { agency: 'Kinesso', agencyId: '001E000000cVeb3IAC' },
  'omnicommediagroup.com': { agency: 'Omnicom Media Group', agencyId: '001Rl00000exIQ5IAM' },
  'mbww.com': { agency: 'MBWW (Omnicom)', agencyId: '001Rl00000exIQ5IAM' },
  'rufusww.com': { agency: 'RufusWW (Omnicom)', agencyId: '001Rl00000exIQ5IAM' }
};

const divisionMapping = {
  'prime video': { accountId: '0010h00001du5MCAAY', brand: 'Prime Video' },
  'alexa': { accountId: '001Rl00000TRSkIIAX', brand: 'Alexa' },
  'ads': { accountId: '001Rl00000703UyIAI', brand: 'Amazon Ads' },
  'business': { accountId: '001Rl0000070ZRbIAM', brand: 'Amazon Business' },
  'ring': { accountId: '0010h00001bqDoyAAE', brand: 'Ring' },
  'aws': { accountId: '0010h00001i0vGdAAI', brand: 'AWS' },
  'fresh': { accountId: '001Rl00000ZhPJyIAN', brand: 'Amazon Fresh' },
  'reputation': { accountId: '0016Q00001wGx6eQAC', brand: 'Amazon Reputation' },
  'xcm': { accountId: '0010h00001iIJvlAAG', brand: 'Amazon XCM' }
};

// Process each email item
const processedItems = [];

for (const item of $input.all()) {
  try {
    // Extract sender domain
    const fromEmail = item.json.payload?.headers?.find(h => h.name === 'From')?.value || '';
    const domainMatch = fromEmail.match(/@([^\s>]+)/)?.[1] || '';

    // Security check - validate authorized domain
    const authorizedDomains = ['amazon.com', 'kinesso.com', 'omnicommediagroup.com', 'mbww.com', 'rufusww.com'];
    if (!authorizedDomains.some(domain => domainMatch.includes(domain))) {
      console.log(`Unauthorized domain detected: ${domainMatch}. Skipping email.`);
      continue;
    }

    // Get agency mapping
    const agencyMapping = Object.entries(domainToAgency).find(([domain]) => 
      domainMatch.includes(domain)
    )?.[1] || { agency: 'Unknown', agencyId: null };

    // Extract email content
    const subject = item.json.payload?.headers?.find(h => h.name === 'Subject')?.value || '';
    const emailContent = item.json.snippet || '';
    const messageId = item.json.id;

    // Extract brand/division from content
    const contentLower = (subject + ' ' + emailContent).toLowerCase();
    const brandMapping = Object.entries(divisionMapping).find(([keyword]) => 
      contentLower.includes(keyword)
    )?.[1] || { accountId: agencyMapping.agencyId, brand: 'General' };

    // Extract campaign name from subject
    const campaignName = subject
      .replace(/^(re:|fwd:|fw:)\s*/i, '')
      .replace(/\b(rfp|proposal|campaign)\b/gi, '')
      .trim() || 'Campaign';

    // Estimate flight dates and quarters
    const now = new Date();
    const currentQuarter = Math.ceil((now.getMonth() + 1) / 3);
    const currentYear = now.getFullYear();

    // Extract budget indicators
    const budgetMatches = emailContent.match(/\$([\d,]+(?:\.\d{2})?)[kmKM]?/g) || [];
    let estimatedBudget = 50000; // Default
    if (budgetMatches.length > 0) {
      const budgetStr = budgetMatches[0].replace(/[$,]/g, '');
      estimatedBudget = parseFloat(budgetStr);
      if (budgetStr.toLowerCase().includes('k')) estimatedBudget *= 1000;
      if (budgetStr.toLowerCase().includes('m')) estimatedBudget *= 1000000;
    }

    // Determine budget tier
    let budgetTier = 'Essential';
    if (estimatedBudget >= 75000) budgetTier = 'Premium';
    else if (estimatedBudget >= 25000) budgetTier = 'Growth';

    // Extract target audience indicators
    const audienceKeywords = ['adults', 'women', 'men', 'millennials', 'gen z', 'parents', 'shoppers'];
    const targetAudience = audienceKeywords.find(keyword => 
      contentLower.includes(keyword)
    ) || 'General Audience';

    // Determine campaign objective
    let campaignObjective = 'Brand Awareness';
    if (contentLower.includes('conversion') || contentLower.includes('sales')) {
      campaignObjective = 'Conversion';
    } else if (contentLower.includes('consideration') || contentLower.includes('intent')) {
      campaignObjective = 'Consideration';
    } else if (contentLower.includes('retention') || contentLower.includes('loyalty')) {
      campaignObjective = 'Retention';
    }

    // Detect tentpole events
    const tentpoles = {
      'prime day': 'Prime Day',
      'black friday': 'Black Friday',
      'cyber monday': 'Cyber Monday',
      'holiday': 'Holiday Season',
      'back to school': 'Back to School',
      'super bowl': 'Super Bowl'
    };

    const tentpoleEvent = Object.entries(tentpoles).find(([keyword]) => 
      contentLower.includes(keyword)
    )?.[1] || null;

    // Determine trading entity
    let tradingEntity = 'Kargo Direct';
    if (agencyMapping.agency.includes('Omnicom')) {
      tradingEntity = 'Kargo via Omnicom';
    } else if (agencyMapping.agency === 'Kinesso') {
      tradingEntity = 'Kargo via Kinesso';
    }

    // Extract attachments info
    const attachments = item.json.payload?.parts?.filter(part => 
      part.filename && part.filename.length > 0
    ) || [];

    const attachmentSummary = attachments.map(att => ({
      filename: att.filename,
      mimeType: att.mimeType,
      size: att.body?.size || 0
    }));

    // Generate opportunity name
    const opportunityName = `${brandMapping.brand}_${campaignName.replace(/\s+/g, '_')}_${currentQuarter}Q${currentYear}`;

    // Determine priority level
    let priorityLevel = 'Medium';
    if (estimatedBudget >= 100000 || brandMapping.brand === 'Prime Video') {
      priorityLevel = 'High';
    } else if (estimatedBudget < 25000) {
      priorityLevel = 'Low';
    }

    // Creative format detection
    const creativeMixKeywords = {
      'video': contentLower.includes('video') || contentLower.includes('tv'),
      'display': contentLower.includes('display') || contentLower.includes('banner'),
      'native': contentLower.includes('native') || contentLower.includes('sponsored'),
      'audio': contentLower.includes('audio') || contentLower.includes('podcast')
    };

    const creativeMix = Object.entries(creativeMixKeywords)
      .filter(([format, detected]) => detected)
      .map(([format]) => format)
      .join(', ') || 'Display';

    // Measurement requirements
    const measurementRequirements = [];
    if (contentLower.includes('attribution')) measurementRequirements.push('Attribution');
    if (contentLower.includes('viewability')) measurementRequirements.push('Viewability');
    if (contentLower.includes('brand lift')) measurementRequirements.push('Brand Lift');
    if (contentLower.includes('incrementality')) measurementRequirements.push('Incrementality');

    // Competitive considerations
    const competitors = ['walmart', 'target', 'google', 'facebook', 'youtube', 'hulu', 'roku'];
    const competitiveConsiderations = competitors.filter(comp => 
      contentLower.includes(comp)
    ).join(', ') || 'Standard competitive landscape';

    // Timeline estimation
    const timelineKeywords = {
      'urgent': contentLower.includes('urgent') || contentLower.includes('asap'),
      'q1': contentLower.includes('q1') || contentLower.includes('first quarter'),
      'q2': contentLower.includes('q2') || contentLower.includes('second quarter'),
      'q3': contentLower.includes('q3') || contentLower.includes('third quarter'),
      'q4': contentLower.includes('q4') || contentLower.includes('fourth quarter')
    };

    let estimatedTimeline = '2-3 weeks';
    if (timelineKeywords.urgent) estimatedTimeline = '1 week';

    // Retail Media Network detection
    let retailMediaNetwork = 'Not Specified';
    if (contentLower.includes('dsp') || contentLower.includes('demand side')) {
      retailMediaNetwork = 'Amazon DSP';
    } else if (contentLower.includes('sponsored') || contentLower.includes('search')) {
      retailMediaNetwork = 'Sponsored Products';
    }

    // Build comprehensive output object
    const processedItem = {
      // Core identification
      messageId,
      domain: domainMatch,
      agency: agencyMapping.agency,
      agencyId: agencyMapping.agencyId,
      accountId: brandMapping.accountId,
      brand: brandMapping.brand,

      // Email content
      subject,
      emailContent,
      fromEmail,
      attachmentSummary,

      // Campaign details
      campaignName,
      opportunityName,
      flightQuarter: `${currentQuarter}Q${currentYear}`,
      tradingEntity,
      retailMediaNetwork,
      tentpoleEvent,

      // Financial
      estimatedBudget,
      budgetTier,

      // Strategic
      campaignObjective,
      targetAudience,
      creativeMix,
      measurementRequirements: measurementRequirements.join(', ') || 'Standard reporting',
      competitiveConsiderations,

      // Timeline and priority
      estimatedTimeline,
      priorityLevel,

      // Processing metadata
      processedAt: now.toISOString(),
      workflowVersion: '1.0',

      // Salesforce preparation
      closeDate: new Date(now.getTime() + (30 * 24 * 60 * 60 * 1000)).toISOString().split('T')[0], // 30 days from now
      stageName: 'Prospect',
      opportunityType: 'New Business',

      // Additional flags
      brainstormRequired: true,
      hasAttachments: attachments.length > 0,
      requiresImmediateAttention: priorityLevel === 'High' || timelineKeywords.urgent
    };

    processedItems.push(processedItem);

    // Log successful processing
    console.log(`Successfully processed RFP from ${agencyMapping.agency} for ${brandMapping.brand}`);

  } catch (error) {
    console.error(`Error processing email ${item.json.id}:`, error.message);
    // Continue processing other emails
  }
}

// Return processed items
return processedItems.map(item => ({ json: item }));

// Error Handler and Audit Logger

const items = $input.all();
const errors = [];
const auditLog = [];

for (const item of items) {
  try {
    const timestamp = new Date().toISOString();
    const logEntry = {
      timestamp,
      workflowId: $workflow.id,
      executionId: $execution.id,
      status: 'success',
      emailId: item.json.messageId || 'unknown',
      agency: item.json.agency || 'unknown',
      brand: item.json.brand || 'unknown',
      opportunityId: item.json.opportunityId || null,
      processedData: {
        budgetTier: item.json.budgetTier,
        priorityLevel: item.json.priorityLevel,
        hasAttachments: item.json.hasAttachments,
        requiresImmediateAttention: item.json.requiresImmediateAttention
      }
    };

    auditLog.push(logEntry);

  } catch (error) {
    const errorEntry = {
      timestamp: new Date().toISOString(),
      workflowId: $workflow.id,
      executionId: $execution.id,
      status: 'error',
      error: error.message,
      stack: error.stack,
      itemData: JSON.stringify(item.json, null, 2)
    };

    errors.push(errorEntry);
  }
}

// Log to console for debugging
console.log('Audit Log:', JSON.stringify(auditLog, null, 2));
if (errors.length > 0) {
  console.error('Errors:', JSON.stringify(errors, null, 2));
}

// Return summary
return [{
  json: {
    processedCount: auditLog.length,
    errorCount: errors.length,
    auditLog,
    errors,
    summary: {
      totalProcessed: auditLog.length,
      successfullyProcessed: auditLog.filter(log => log.status === 'success').length,
      errorsEncountered: errors.length,
      lastProcessedAt: new Date().toISOString()
    }
  }
}];
